mapper.select:
/*status = 0表示数据是有效的*/
select id, user_id, entity_type, entity_id, target_id, content, status, create_time
from community.comment
where status = 0 and entity_type = #{entityType} and entity_id = #{entityId}
order by create_time
limit #{offset},#{limit}

select count(id)
from community.comment
where status = 0 and entity_type = #{entityType} and entity_id = #{entityId}
