mapper.select:
select id, user_id, title, content, type, status, create_time, comment_count, score
from community.discuss_post

    and status != 2

        and user_id = #{userId}


order by type desc,create_time desc
limit #{offset},#{limit}

select count(id) from community.discuss_post
and status != 2
and user_id = #{userId}

select id, user_id, title, content, type, status, create_time, comment_count, score
from community.discuss_post
where id = #{id}

mapper.update:
